
import { Input, GetProps, Image, Button, message } from 'antd';
import styles from './Encode.module.scss'
import { DownloadOutlined } from '@ant-design/icons';
import Footer from '../Footer';
type SearchProps = GetProps<typeof Input.Search>;
const { Search } = Input;
import { QRCodeCanvas, QRCodeSVG } from 'qrcode.react';
import React, { useState, useRef } from 'react';
import * as htmlToImage from 'html-to-image';
import { toPng, toJpeg, toBlob, toPixelData, toSvg } from 'html-to-image';
const Encode = () => {
    const [inputvalue, setInputvalue] = useState("")
    const qrelement = useRef(null)
    const onSearch: SearchProps['onSearch'] = (value, _e, info) => {
        setInputvalue(value)
    }
    const download = () => {
        console.log(qrelement);
        if (qrelement.current) {
            htmlToImage.toJpeg(qrelement.current,{
                quality: 1,
                pixelRatio: window.devicePixelRatio
            })
                .then(function (dataUrl) {
                    // console.log(dataUrl);
                    const link = document.createElement('a');
                    link.download = 'qrcode.png';
                    link.href = dataUrl;
                    link.click();
                })
                .catch(function (error) {
                    message.error('oops, something went wrong!', error);
                })
        }


    }
    return (
        <>
            <div className={styles.search_box}>
                <Search
                    placeholder="input content text"
                    allowClear
                    enterButton="Generate"
                    // size="small"
                    onSearch={onSearch}
                />
            </div>

            <div className={styles.img_box}>
                {/* <Image
                    width={256}
                    height={256}
                    src=""
                    alt='QR code'
                    placeholder={true}
                    preview={false}
                    fallback="data:image/png;base64,"
                /> */}
                <QRCodeCanvas
                    ref={qrelement}
                    value={inputvalue}
                    style={{ width: "200px", height: "200px" }}
                    marginSize={1}
                />
                <div className={styles.img_text}>
                    <Button type="dashed" icon={<DownloadOutlined />} style={{ marginBottom: "12px" }} onClick={download}>
                        Download
                    </Button>
                    <br></br>
                    <Button type="link" size='small' onClick={gohref}>
                        Customize QR Code here
                    </Button>
                </div>
            </div>
            <div className={styles.footer}>
                <Footer />
            </div>
        </>
    )

}

const gohref = () => {
    const url = browser.runtime.getURL('/newpage.html')
    // browser.Tabs.
    browser.tabs.create({
        url: url
    })
    // console.log(url);


}

export default Encode;